import { useState } from 'react';

function App() {
  const [isLoading] = useState(false);
  const [showMyAccount] = useState(false);
  const [usernameState] = useState('User');
  const [profileImage] = useState('data:image/jpeg;base64,');

  return (
    <div>
      <h1>App Component</h1>
      {showMyAccount && <p>Account settings</p>}
      {isLoading && <p>Loading...</p>}
      <p>Welcome, {usernameState}</p>
      <img src={profileImage} alt="Profile" />
    </div>
  );
}

export default App;
